const init=()=>{const e=document.querySelector(".nav_component.w-nav");let t=0,n=window.pageYOffset;document.addEventListener("scroll",()=>{var i=window.pageYOffset;992<window.innerWidth?i>n?(e.style.visibility="hidden",t=0):(20<=++t||i<=5)&&(e.style.visibility="visible"):e.style.visibility="visible",n=i}),window.addEventListener("resize",()=>{window.innerWidth<=992&&(e.style.visibility="visible")})};init();